mapper.select:
SELECT QUIZ_ANSWER
FROM DAILY_QUIZ
WHERE QUIZ_NO = #{quizNo}

mapper.insert:
INSERT INTO DAILY_QUIZ (
    QUIZ_NO,
    QUIZ_QUESTION,
    QUIZ_ANSWER
)
VALUES (
    SEQ_DAILY_QUIZ_NO.NEXTVAL,
    #{quizQuestion},
    #{quizAnswer}
)

mapper.update:
UPDATE DAILY_QUIZ
SET
    QUIZ_QUESTION = #{quizQuestion},
    QUIZ_ANSWER   = #{quizAnswer}
WHERE QUIZ_NO = #{quizNo}
